function ChangeClassLeft(){document.getElementById("left").classList.toggle("left_show")}function ChangeClassTOC(){document.getElementById("left").classList.remove("left_show"),document.getElementById("toc").classList.toggle("toc_show")}function HideBothSide(){document.getElementById("left").classList.remove("left_show"),document.getElementById("toc").classList.remove("toc_show")}function ChangeClassBlackBlock(a){a.classList.toggle("black_block_show")}function ut_change_to_light_mode(){try{const b={type:'set-theme',theme:'github-light'};var a=document.querySelector('iframe');a.contentWindow.postMessage(b,'https://utteranc.es')}catch(a){}}function ut_change_to_dark_mode(){try{const b={type:'set-theme',theme:'github-dark'};var a=document.querySelector('iframe');a.contentWindow.postMessage(b,'https://utteranc.es')}catch(a){}}function themeSwitch(){checkbox.checked?(themeContainer.classList.remove("dark"),localStorage.setItem("ctheme","light"),ut_change_to_light_mode()):(themeContainer.classList.add("dark"),localStorage.setItem("ctheme","dark"),ut_change_to_dark_mode())}const checkbox=document.querySelector(".theme-switcher");checked===1?(checkbox.checked=!0,ut_change_to_light_mode()):checked===0&&(checkbox.checked=!1,ut_change_to_dark_mode())